mapper.select:
select * from
	(select *from
		(select rep_num, userid, rep_subject , rep_status, to_char(rep_writedate, 'MM-DD HH:MI') rep_writedate from ${cate}
		
			where ${searchKey} like #{searchWord}
		
		order by ${num} desc)
			where rownum <= (${pageNum}*${onePageRecord}) order by ${num} )
			where rownum <=
			
				${lastPageRecord}
			
			
				${onePageRecord}
			
			order by ${num} desc

select cs_num, userid, cs_cate, cs_subject, cs_content, cs_status, cs_answer, 
to_char(cs_writedate, 'MM-DD HH:MI') cs_writedate, to_char(cs_date, 'MM-DD HH:MI') cs_date 
from cs where cs_num=#{param1}
