mapper.select:
  		select * from
			(select *from
				(select mem_no, userid, mem_name, mem_addr, mem_tel, mem_status, mem_type from ${cate}
				
					where ${searchKey} like #{searchWord}
				
				order by ${num} desc)
					where rownum <= ${pageNum}*${onePageRecord} order by ${num} )
					where rownum <=
					
						${lastPageRecord}
					
					
						${onePageRecord}
					
					order by ${num} desc

select * from (select *from (select s.sel_num, s.userid ,s.sel_name, s.sel_addr,s.sel_detail, s.sel_lvl,s.sel_tel, m.mem_status, m.mem_type from seller s join member m on s.userid = m.userid
where ${searchKey} like #{searchWord}
order by ${num} desc) where rownum <= ${pageNum}*${onePageRecord} order by ${num} ) where rownum <=
${lastPageRecord}
${onePageRecord}
order by ${num} desc

select * from (select *from (select m.num , m.s_cate ,m.s_subject ,m.s_cnt ,m.userid , to_char(m.s_writedate,'YYYY/MM/DD') s_writedate, count(case when r.num=m.num then 1 end) reportCount from mem_share m, report r
where ${searchKey} like #{searchWord}
group by m.num, m.s_cate,m.s_subject,m.s_cnt,m.userid, m.s_writedate order by num desc ) where rownum <= ${pageNum}*${onePageRecord} order by ${num} ) where rownum <=
${lastPageRecord}
${onePageRecord}
order by ${num} desc

select * from (select *from (select i.i_num, i.i_status, i.i_subject,i.i_cnt, s.userid ,to_char(i.i_writedate, 'YYYY/MM/DD') i_writedate,i.i_hit from sell_item i join seller s on i.sel_num = s.sel_num
where ${searchKey} like #{searchWord}
order by ${num} desc) where rownum <= ${pageNum}*${onePageRecord} order by ${num} ) where rownum <=
${lastPageRecord}
${onePageRecord}
order by ${num} desc

select * from (select *from (select g.num,g.up_cate,g.down_cate,g.g_subject,g.g_cnt,g.userid, to_char(g.g_writedate,'YYYY-MM-DD') g_writedate, count(case when r.num=g.num then 1 end) reportCount from grouplocal g, report r
where ${searchKey} like #{searchWord}
group by g.num,g.up_cate,g.down_cate,g.g_subject,g.g_cnt,g.userid, g.g_writedate order by g.num desc ) where rownum <= ${pageNum}*${onePageRecord} order by ${num} ) where rownum <=
${lastPageRecord}
${onePageRecord}
order by ${num} desc

select * from (select *from (select b.num, b.up_cate, b.b_gu,b.b_subject,b.userid,b.b_hit, to_char(b.b_writedate,'YYYY-MM-DD') b_writedate, count(case when r.num=b.num then 1 end) reportCount from board b, report r
where ${searchKey} like #{searchWord}
group by b.num, b.up_cate, b.b_gu, b.b_subject, b.userid, b.b_writedate, b.b_hit order by b.num desc ) where rownum <= ${pageNum}*${onePageRecord} order by ${num} ) where rownum <=
${lastPageRecord}
${onePageRecord}
order by ${num} desc

  		 select * from
			(select *from
				(select ch3.userid, ch3.charge_point, ch3.spend_point, sum(sp2.sp_point) as add_point from 
					 (select ch2.userid, ch2.charge_point,ch2.spend_point ,mem.num from
					 (select ch.userid, ch.charge_point, sum(sp.sp_point) as spend_point from
					 (select userid,sum(cha_point) as charge_point  from cha_p group by userid) ch 
					 join sp_p sp on sp.userid(+)=ch.userid
					 group by ch.userid, ch.charge_point) ch2 join mem_share mem on mem.userid(+)=ch2.userid) ch3 
					 join sp_p sp2 on sp2.sp_item(+)=ch3.num 
					 
					 
						where ${searchKey} like #{searchWord}
					
					 
					 group by ch3.userid, ch3.charge_point,ch3. spend_point
  					 order by ${num} 
  					)
					where rownum <= ${pageNum}*${onePageRecord} order by ${num} desc)
					where rownum <=
							
								${lastPageRecord}
							
							
								${onePageRecord}
							
							order by ${num}

  		select * from
			(select *from
				(select sel1.sel_num, sel1.sel_userid, sel1.sel_subject, sum(o.o_cnt) as sel_cnt,sum(o_price) as sel_price, sum(o.o_cnt)*2000 as fee,sum(o_price)-sum(o.o_cnt)*2000 as remoney from 
					(select i.i_num as sel_num, sel.userid as sel_userid, i.i_subject as sel_subject from sell_item i join seller sel on sel.sel_num=i.sel_num) sel1
					join ordertbl o on o.num(+)=sel1.sel_num where o_date between to_date('21/${month}/01','YY/MM/DD') and to_date(last_day('21/${month}/01'),'YY/MM/DD')
					
						and ${searchKey} like #{searchWord}
					
					group by sel1.sel_num, sel1.sel_userid, sel1.sel_subject order by sel_num 
					)
					where rownum <= ${pageNum}*${onePageRecord} order by sel_num desc)
					where rownum <=
							
								${lastPageRecord}
							
							
								${onePageRecord}
							
							order by sel_num
