mapper.select:
select count(num) from board  where

	 b_gu=#{b_gu} and up_cate=${up_cate}


	up_cate=${up_cate}

 
	up_cate=${up_cate} 


	and ${searchKey} like '%${searchWord}%'

	    select * from        
	        (select * from               
	            (select * from         
	                (select num,userid, b_gu, up_cate, down_cate,b_subject, b_content ,b_tag,to_char(b_writedate, 'YYYY.MM.DD') b_writedate ,b_hit,
					to_date(sysdate,'YYYY.MM.DD') - to_date(b_writedate,'YYYY.MM.DD') newDate, 
					(select count(*) from reply where num = b.num) repcont,
    				(select count(*) from likeit where num = numlike) numlike  from board b
	                
	                
	                    where ${searchKey} like '%${searchWord}%'and up_cate=${up_cate}
	                    
	                      and b_gu=#{b_gu}
	                    
	                    
	                 order by num desc)
	                
	           
	         	   where up_cate=${up_cate} 
	         	    
	         	    
	                      and b_gu=#{b_gu}
	                
	           )
	                                
	        where rownum<=${pageNum * onePageRecord}  order by num asc)
	    where rownum<=
	                 
	    
	    	${lastPageRecord}
	    
	            
	    
	    	${onePageRecord}
	    
	    order by num desc

mapper.update:
update board set b_subject=#{b_subject}, b_content=#{b_content}, 

	b_img1=#{b_img1},

b_tag=#{b_tag} where num=#{num} and userid=#{userid}

mapper.select:
select * from( select num, lag(num,1,0) over (order by num ) prevNo, lag(b_subject, 1, '이전글이 없습니다') over(order by num ) prevSubject, lead(num,1,0) over (order by num ) nextNO, lead(b_subject,1,'다음글이 없습니다.') over (order by num ) nextSubject from (select * from (select * from (select num,up_cate,userid,b_gu,b_subject,b_content from board)
where #{searchKey} like '%${searchWord}%'
order by num desc)
where up_cate=${up_cate}
) board order by num) where num = ${num}
